package com.twjitm.core.common.service.rpc.server;/** * @author twjitm - [Created on 2018-08-20 11:03] * @company https://github.com/twjitm/ * @jdk java version "1.8.0_77" */public class NettyRpcNodeInfo {    /**     * 服务器id     */    private String serverId;    /**     * 服务ip     */    private String host;    /**     * 端口     */    private String port;    public String getServerId() {        return serverId;    }    public void setServerId(String serverId) {        this.serverId = serverId;    }    public String getHost() {        return host;    }    public void setHost(String host) {        this.host = host;    }    public String getPort() {        return port;    }    public void setPort(String port) {        this.port = port;    }    public int getIntPort(){        return Integer.parseInt(port);    }}