package com.twjitm.core.common.system;/** * @author twjitm - [Created on 2018-08-17 10:55] * @company https://github.com/twjitm/ * @jdk java version "1.8.0_77" */public class System {    private SystemType type;    private String version;    public enum SystemType {        LINUX(0, "linux"),        WINDOWS(1, "windows"),        MAC(2, "mac");        private int value;        private String name;        SystemType(int value, String name) {            this.value = value;            this.name = name;        }    }    public SystemType getType() {        return type;    }    public void setType(SystemType type) {        this.type = type;    }    public String getVersion() {        return version;    }    public void setVersion(String version) {        this.version = version;    }}