package com.twjitm.core.utils.time.timer;import java.util.Set;import java.util.concurrent.ConcurrentSkipListSet;/** * 时间槽 * * @author twjitm - [Created on 2018-08-31 10:34] * @company https://github.com/twjitm/ * @jdk java version "1.8.0_77" */public class TimeSlot<E> {    /**     * 时间槽id     */    private int id;    /**     * 槽元素     */    private Set<E> elements = new ConcurrentSkipListSet<E>();    public TimeSlot(int id) {        this.id = id;    }    public void add(E e) {        elements.add(e);    }    public boolean remove(E e) {        return elements.remove(e);    }    public Set<E> getElements() {        return elements;    }    @Override    public int hashCode() {        final int prime = 31;        int result = 1;        result = prime * result + id;        return result;    }    @Override    public boolean equals(Object obj) {        if (this == obj) {            return true;        }        if (obj == null) {            return false;        }        if (getClass() != obj.getClass()) {            return false;        }        TimeSlot timeSlot = (TimeSlot) obj;        if (timeSlot.id != id) {            return false;        }        return true;    }}